import React from 'react';

interface MenuItemProps {
  item: MenuItemType;
  onAdd: () => void;
  onToggleExpand: () => void;
  isExpanded: boolean;
}

const MenuItem: React.FC<MenuItemProps> = ({ item, onAdd, onToggleExpand, isExpanded }) => {
  return (
    <div className="flex items-center relative">
      {/* Chevron Icon */}
      <div onClick={onToggleExpand} className="cursor-pointer mr-2">
        {isExpanded ? (
          <svg
            xmlns="http://www.w3.org/2000/svg"
            className="h-4 w-4"
            viewBox="0 0 20 20"
            fill="currentColor"
          >
            <path
              fillRule="evenodd"
              d="M5.293 7.293a1 1 0 011.414 0L10 10.586l3.293-3.293a1 1 0 111.414 1.414l-4 4a1 1 0 01-1.414 0l-4-4a1 1 0 010-1.414z"
              clipRule="evenodd"
            />
          </svg>
        ) : (
          <svg
            xmlns="http://www.w3.org/2000/svg"
            className="h-4 w-4"
            viewBox="0 0 20 20"
            fill="currentColor"
          >
            <path
              fillRule="evenodd"
              d="M7.293 5.293a1 1 0 011.414 0L10 7.586l1.293-1.293a1 1 0 011.414 1.414l-2 2a1 1 0 01-1.414 0l-2-2a1 1 0 010-1.414z"
              clipRule="evenodd"
            />
          </svg>
        )}
      </div>

      {/* Menu Item Name */}
      <span className="font-semibold">{item.name}</span>

      {/* Add Button */}
      <button
        onClick={onAdd}
        className="ml-2 p-1 bg-blue-500 text-white rounded hover:bg-blue-600"
      >
        Add
      </button>

      {/* Vertical Line for Child Items */}
      {item.children && item.children.length > 0 && (
        <div className="absolute left-0 top-full w-px h-4 bg-gray-300"></div>
      )}
    </div>
  );
};

export default MenuItem;
