import React, { useState, useEffect } from 'react';
import Input from '../atoms/Input';
import Button from '../atoms/Button';
import Label from '../atoms/Label';

interface AddMenuFormProps {
  menuId: string;
  depth: number;
  parentData: string;
  onSave: () => void;
}

const AddMenuForm: React.FC<AddMenuFormProps> = ({ menuId, depth, parentData, onSave }) => {
  const [name, setName] = useState<string>('');

  useEffect(() => {
    // Reset the name when the form is re-rendered for a new item
    setName('');
  }, [menuId, parentData]);

  const handleSave = () => {
    // Logic to save the menu item
    // You would typically call an API here
    console.log('Saving menu item:', { menuId, depth, parentData, name });
    onSave();
  };

  return (
    <div className="flex flex-col p-4 space-y-2">
      <Label>MenuID</Label>
      <Input value={menuId} disabled />
      <Label>Depth</Label>
      <Input value={depth.toString()} disabled className='w-[50%]' />
      <Label>Parent Data</Label>
      <Input value={parentData} disabled className='w-[50%]' />
      <Label>Name</Label>
      <Input 
        value={name} 
        onChange={(e) => setName(e.target.value)} 
        className='w-[50%]'
      />
      <Button 
        onClick={handleSave} 
        backgroundColor='#253BFF' 
        className='text-white mr-2 px-[32px] py-[14px] w-[50%]'  
        disabled={!name.trim()}
      >
        Save
      </Button>
    </div>
  );
};

export default AddMenuForm;
