import React, { useState } from 'react';
import Input from '../atoms/Input';
import Select from '../atoms/Select';
import Button from '../atoms/Button';
import Label from '../atoms/Label';

const AddMenuForm: React.FC = () => {
  const [menuId, setMenuId] = useState<string>('');
  const [depth, setDepth] = useState<number>(0);
  const [parentData, setParentData] = useState<string>('');
  const [name, setName] = useState<string>('');

  const handleSave = () => {
    // Logic to save the menu item
  };

  return (
    <div className="flex flex-col p-4 space-y-2">
       <Label>MenuID</Label>
      <Input value={menuId} onChange={(e) => setMenuId(e.target.value)} disabled />
      <Label>Depth</Label>
      <Input
        value={depth.toString()}
        onChange={(e) => setDepth(parseInt(e.target.value, 10))}
      />
       <Label>Parent Data</Label>
      <Input
        value={parentData}
        onChange={(e) => setParentData(e.target.value)}
        className='w-[50%]'
      />
       <Label>Name</Label>
      <Input value={name} onChange={(e) => setName(e.target.value)} className='w-[50%]'/>
      <Button onClick={handleSave} backgroundColor='#253BFF' className='text-white mr-2 px-[32px] py-[14px]' >Save</Button>
    </div>
  );
};

export default AddMenuForm;
