import React, { useState } from 'react';
import Sidebar from '../organisms/Sidebar';
import MenusSection from '../organisms/MenusSection';
import AddMenuForm from '../organisms/AddMenuForm';

const Layout: React.FC = () => {
  const [isSidebarOpen, setIsSidebarOpen] = useState<boolean>(true);

  return (
    <div className="flex h-screen flex-row">
      <Sidebar isOpen={isSidebarOpen} onToggle={() => setIsSidebarOpen(!isSidebarOpen)} />
      <div className="flex">
      <header className="flex justify-between items-center p-4 bg-white border-b border-gray-250 border-[1px]">
          <button onClick={() => setIsSidebarOpen(true)} className="lg:hidden">
            <IconMenu2 size={24} />
          </button>
          <h1 className="text-xl font-[500] text-[24px] leading-[29px] ml-4 ">Reviews</h1>
        </header>
        <MenusSection />
        {isSidebarOpen && <AddMenuForm />}
      </div>
    </div>
  );
};

export default Layout;
