import React, { useState } from "react";
import { useMenus, useMenuItems, useAddMenuItem } from "../hooks/apiHooks";
import Button from "./atoms/Button";
import SelectMenu from "../components/SelectMenu";
import Icon from "../components/Icon";

const MenusSection: React.FC = () => {
  const { data: menus } = useMenus();
  const [selectedMenu, setSelectedMenu] = useState<string>("");
  const { data: menuItems } = useMenuItems(selectedMenu);
  const addMenuItem = useAddMenuItem(selectedMenu);

  const handleMenuChange = (e: React.ChangeEvent<HTMLSelectElement>) => {
    setSelectedMenu(e.target.value);
  };

  const handleAddItem = () => {
    if (selectedMenu) {
      addMenuItem.mutate({
        name: "New Item",
        depth: 1,
        parent_id: null,
        menuId: selectedMenu,
      });
    }
  };

  return (
    <div className="p-4">
      <div className="flex items-center mb-4">
        <Icon name="menu" />
        <h1 className="ml-2 text-xl">Menus</h1>
      </div>
      <SelectMenu
        options={
          menus?.map((menu) => ({
            label: menu.name,
            value: menu.id,
          })) || []
        }
        onChange={handleMenuChange}
        value={selectedMenu}
      />
      <div className="flex space-x-2">
        <Button
          onClick={() => {
            /* Implement Expand All Logic */
          }}
        >
          Expand All
        </Button>
        <Button
          onClick={() => {
            /* Implement Collapse All Logic */
          }}
        >
          Collapse All
        </Button>
      </div>
      <div className="mt-4">
        <ul>
          {menuItems?.map((item) => (
            <li key={item.id}>
              <div className="flex items-center">
                <span className="mr-2">â–¶</span> {item.name}
                <button className="ml-auto" onClick={handleAddItem}>
                  +
                </button>
              </div>
              {/* Recursive rendering of child items if any */}
            </li>
          ))}
        </ul>
      </div>
    </div>
  );
};

export default MenusSection;
