import React, { useState } from 'react';
import Button from '../atoms/Button';
import Select from '../atoms/Select';
import MenuList from '../molecules/MenuList';
import { useMenus, MenuItem as MenuItemType } from '../../hooks/apiHooks';
import AddMenuForm from './AddMenuForm';
import Label from '../atoms/Label';

const MenusSection: React.FC = () => {
  const { data: menus } = useMenus();
  const [selectedMenu, setSelectedMenu] = useState<string>('');
  const [menuItems, setMenuItems] = useState<MenuItemType[]>([]);
  const [isExpanded, setIsExpanded] = useState<boolean>(false);

  const handleExpandCollapse = (expand: boolean) => {
    setIsExpanded(expand);
    // Logic to expand/collapse the menu items
  };

  return (
    <div className="flex flex-col p-4">
      <div className="flex flex-col mt-4">
        <Label>Menu</Label>
        <Select
          value={selectedMenu}
          onChange={(e) => setSelectedMenu(e.target.value)}
          options={menus?.map((menu) => ({ label: menu.name, value: menu.id })) || []}
          className="w-[349px] mb-5"
        />
        <div className="flex">
            <div className="w-[50%]">
                <Button onClick={() => handleExpandCollapse(true)} backgroundColor='#1D2939' className='text-white mr-2 px-[32px] py-[12px]'>Expand All</Button>
                <Button onClick={() => handleExpandCollapse(false)} backgroundColor='transparent' className='border-[#D0D5DD] border-[1px] px-[32px] py-[12px]'>Collapse All</Button>
                <MenuList items={menuItems} onAdd={(id) => {/* Logic to show add form */}} />
                <Button onClick={()=> {}} backgroundColor='#253BFF' className='text-white mr-2 px-[32px] py-[14px] w-[50%]' ></Button>
            </div>
            <div className="w-[50%]">
                <AddMenuForm />
            </div>
        </div>

      </div>
    </div>
  );
};

export default MenusSection;
