import React, { useState } from 'react';
import { MenuItem as MenuItemType } from '../../hooks/apiHooks';
import ChevronIcon from '../atoms/ChevronIcon';

interface MenuItemProps {
  item: MenuItemType;
  onAdd: () => void;
  onToggleExpand: () => void;
  isExpanded: boolean;
  isLastChild?: boolean; // Optional prop to detect if this is the last child
}

const MenuItem: React.FC<MenuItemProps> = ({ item, onAdd, onToggleExpand, isExpanded, isLastChild }) => {
  const [isHovered, setIsHovered] = useState<boolean>(false);

  return (
    <div 
      className="flex items-center relative"
      onMouseEnter={() => setIsHovered(true)}
      onMouseLeave={() => setIsHovered(false)}
    >
      {item.parent_id && (
        <div 
          className={`w-5 h-px bg-[#98A2B3] mt-4 ml-[-1.7rem] mr-2 ${isLastChild && isExpanded ? 'shortened-line' : ''}`}
        ></div>
      )}

      {/* Chevron Icon */}
      <div onClick={onToggleExpand} className="cursor-pointer mr-2">
        {item.children && item.children.length > 0 && (
          <ChevronIcon isExpanded={isExpanded} />
        )}
      </div>

      {/* Menu Item Name */}
      <span className="font-[400] text-[14px] leading-[14px] mt-4">{item.name}</span>

      {/* Add Button (Absolute Positioning) */}
      {isHovered && (
        <button
          onClick={onAdd}
          className="ml-2 absolute right-[0px] top-1/2 transform -translate-y-1/2 bg-[#253BFF] text-white rounded-full hover:bg-blue-600 w-[26px] h-[26px]"
        >
          +
        </button>
      )}
    </div>
  );
};

export default MenuItem;
