import React, { useState } from 'react';
import Button from '../atoms/Button';
import Select from '../atoms/Select';
import MenuList from '../molecules/MenuList';
import { useMenus, useAddMenu, MenuItem as MenuItemType } from '../../hooks/apiHooks';
import AddMenuForm from './AddMenuForm';
import Label from '../atoms/Label';
import Modal from '../molecules/Modal';
import Input from '../atoms/Input';

const MenusSection: React.FC = () => {
  const { data: menus } = useMenus();
  const [selectedMenu, setSelectedMenu] = useState<string>('');
  const [menuItems, setMenuItems] = useState<MenuItemType[]>([]);
  const [isExpanded, setIsExpanded] = useState<boolean>(false);
  const [isModalOpen, setIsModalOpen] = useState<boolean>(false);
  const [newMenuName, setNewMenuName] = useState<string>('');

  const addMenuMutation = useAddMenu();

  const handleExpandCollapse = (expand: boolean) => {
    setIsExpanded(expand);
    // Logic to expand/collapse the menu items
  };

  const handleCreateMenu = () => {
    addMenuMutation.mutate(
      { name: newMenuName }, // Add more fields if necessary
      {
        onSuccess: () => {
          setIsModalOpen(false);
          setNewMenuName('');
          // Display success message, reset form, etc.
        },
        onError: (error) => {
          // Display error message to the user
          console.error("Error creating menu:", error);
        }
      }
    );
  };

  return (
    <div className="flex flex-col p-4">
      <div className="flex flex-col mt-4">
        <Label>Menu</Label>
        <Select
          value={selectedMenu}
          onChange={(e) => setSelectedMenu(e.target.value)}
          options={menus?.map((menu) => ({ label: menu.name, value: menu.id })) || []}
          className="max-md:w-full w-[50%] mb-5"
        />
        <div className="flex flex-col lg:flex-row">
          <div className="lg:w-[50%]">
            <Button
              onClick={() => handleExpandCollapse(true)}
              backgroundColor='#1D2939'
              className='text-white mr-2 px-[32px] py-[12px]'
            >
              Expand All
            </Button>
            <Button
              onClick={() => handleExpandCollapse(false)}
              backgroundColor='transparent'
              className='border-[#D0D5DD] border-[1px] px-[32px] py-[12px]'
            >
              Collapse All
            </Button>
            <MenuList items={menuItems} onAdd={(id) => {/* Logic to show add form */}} />
            <Button
              onClick={() => setIsModalOpen(true)}
              backgroundColor='#253BFF'
              className='text-white mr-2 px-[32px] py-[14px] mt-4'
            >
              Create Menu
            </Button>
          </div>
          <div className="lg:w-[50%] mt-4 lg:mt-0">
            <AddMenuForm />
          </div>
        </div>
      </div>
      <Modal isOpen={isModalOpen} onClose={() => setIsModalOpen(false)}>
        <Label>Menu Name</Label>
        <Input
            value={newMenuName}
            onChange={(e) => setNewMenuName(e.target.value)}
            className='w-full'
        />
        <Button
          onClick={handleCreateMenu}
          backgroundColor='#253BFF'
          className='text-white mt-4 w-full px-[32px] py-[14px]'
          disabled={addMenuMutation.isLoading} // Disable button during loading
        >
          {addMenuMutation.isLoading ? "Creating..." : "Submit"}
        </Button>
        {addMenuMutation.isError && (
          <div className="text-red-500 mt-2">
            Error creating menu: {addMenuMutation.error.message}
          </div>
        )}
        {addMenuMutation.isSuccess && (
          <div className="text-green-500 mt-2">
            Menu created successfully!
          </div>
        )}
      </Modal>
    </div>
  );
};

export default MenusSection;
