import React, { useState } from 'react';
import Sidebar from '../organisms/Sidebar';
import folder_gray from '../../assets/folder-gray.svg'
import menu_svg from '../../assets/menu.svg'
import MenusSection from '../organisms/MenusSection';
import AddMenuForm from '../organisms/AddMenuForm';

const Layout: React.FC = () => {
  const [isSidebarOpen, setIsSidebarOpen] = useState<boolean>(true);

  return (
    <div className="flex h-screen flex-row w-screen">
      <Sidebar isOpen={isSidebarOpen} onToggle={() => setIsSidebarOpen(!isSidebarOpen)} />
      <div className="flex flex-col w-full pl-3">
        <header className="flex justify-between items-center p-4 w-full pt-5 mt-4">
            <span> <img src={folder_gray} className='inline-block' /> <span className='text-[#D0D5DD] ml-1'>/</span><span className="text-[#101828] font-[500] text-[14px] leading-[14px] ml-1">Menus</span></span>
        </header>
        <div className="flex justify-between items-center p-4 w-full pt-5 mt-4">
            <span> <img src={menu_svg} className='inline-block' /><span className="text-[#101828] font-[800] text-[32px] leading-[40px] ml-3 pt-5">Menus</span></span>
        </div>
        {/* <MenusSection />
        {isSidebarOpen && <AddMenuForm />} */}
      </div>
    </div>
  );
};

export default Layout;
