import React from 'react';

interface InputProps {
  value: string;
  onChange: (e: React.ChangeEvent<HTMLInputElement>) => void;
  disabled?: boolean;
  className?: string;
}

const Input: React.FC<InputProps> = ({ value, onChange, disabled, className }) => (
  <input
    type="text"
    value={value}
    onChange={onChange}
    disabled={disabled}
    className={`p-4 rounded-[16px] bg-[#F9FAFB] w-full appearance-none pr-10 ${className}`}
  />
);

export default Input;
