import React, { useState } from 'react';
import Button from '../atoms/Button';
import Input from '../atoms/Input';
import Label from '../atoms/Label';

interface AddMenuFormProps {
  menuId: string;
  parentId: string;
  depth: number;
  onSave: (name: string) => void;
}

const AddMenuForm: React.FC<AddMenuFormProps> = ({ menuId, parentId, depth, onSave }) => {
  const [name, setName] = useState('');

  const handleSubmit = () => {
    onSave(name);
  };

  return (
    <div>
      <Label>Menu ID: {menuId}</Label>
      <Label>Parent ID: {parentId}</Label>
      <Label>Depth: {depth}</Label>
      <Input
        value={name}
        onChange={(e) => setName(e.target.value)}
        className="w-full mb-4"
      />
      <Button onClick={handleSubmit} backgroundColor="#253BFF" className="text-white">
        Save
      </Button>
    </div>
  );
};

export default AddMenuForm;
