import React from 'react';
import MenuItem from './MenuItem';
import { MenuItem as MenuItemType } from '../../hooks/apiHooks';

interface MenuListProps {
  items: MenuItemType[];
  onAdd: (item: MenuItemType) => void;
  onExpandCollapse: (itemId: string, expand: boolean) => void;
}

const MenuList: React.FC<MenuListProps> = ({ items, onAdd, onExpandCollapse }) => {
  const renderItems = (items: MenuItemType[], depth = 0) => {
    return items.map((item) => (
      <div key={item.id} style={{ marginLeft: `${depth * 20}px` }}>
        <MenuItem 
          item={item} 
          onAdd={() => onAdd(item)} 
          onExpandCollapse={onExpandCollapse}
        />
        {item.isExpanded && item.children && renderItems(item.children, depth + 1)}
      </div>
    ));
  };

  return <div className="space-y-2">{renderItems(items)}</div>;
};

export default MenuList;
