import React from 'react';
import { MenuItem as MenuItemType } from '../../hooks/apiHooks';
import Button from '../atoms/Button';

interface MenuItemProps {
  item: MenuItemType;
  onAdd: () => void;
  onExpandCollapse: (itemId: string, expand: boolean) => void;
}

const MenuItem: React.FC<MenuItemProps> = ({ item, onAdd, onExpandCollapse }) => (
  <div className="flex items-center justify-between">
    <div className="flex items-center">
      {item.children && item.children.length > 0 && (
        <Button
          onClick={() => onExpandCollapse(item.id, !item.isExpanded)}
          className="mr-2 px-2 py-1"
        >
          {item.isExpanded ? '-' : '+'}
        </Button>
      )}
      <span>{item.name}</span>
    </div>
    <Button onClick={onAdd} className="ml-2 px-2 py-1">
      +
    </Button>
  </div>
);

export default MenuItem;
