import React, { useState } from 'react';
import Sidebar from '../organisms/Sidebar';
import MenusSection from '../organisms/MenusSection';
import AddMenuForm from '../organisms/AddMenuForm';

const Layout: React.FC = () => {
  const [isSidebarOpen, setIsSidebarOpen] = useState<boolean>(true);

  return (
    <div className="flex h-screen flex-row w-screen">
      <Sidebar isOpen={isSidebarOpen} onToggle={() => setIsSidebarOpen(!isSidebarOpen)} />
      <div className="flex flex-col w-full">
        <header className="flex justify-between items-center p-4 bg-white border-b border-gray-250 border-[1px] w-full">
          <h1 className="text-[] font-[500] text-[24px] leading-[29px] ml-4 ">Reviews</h1>
        </header>
        {/* <MenusSection />
        {isSidebarOpen && <AddMenuForm />} */}
      </div>
    </div>
  );
};

export default Layout;
