import React from 'react';
import Input from '../atoms/Input';
import Button from '../atoms/Button';
import Label from '../atoms/Label';

interface AddNewMenuItemFormProps {
  menuId: string;
  parentItem: { id: string; name: string; depth: number } | null;
  newMenuItemName: string;
  setNewMenuItemName: (name: string) => void;
  onSave: () => void;
  onClose: () => void;
}

const AddNewMenuItemForm: React.FC<AddNewMenuItemFormProps> = ({
  parentItem,
  newMenuItemName,
  setNewMenuItemName,
  onSave,
  onClose,
}) => {
  return (
    <div>
      <div className="flex justify-between items-center mb-4">
        <h2>Create New Menu Item</h2>
        <Button onClick={onClose} className="text-sm">
          Close
        </Button>
      </div>
      <Label>Parent Item</Label>
      <Input value={parentItem?.name || ''} disabled className="w-full mb-4" />
      <Label>Depth</Label>
      <Input value={parentItem ? parentItem.depth + 1 : 0} disabled className="w-full mb-4" />
      <Label>Item Name</Label>
      <Input
        value={newMenuItemName}
        onChange={(e) => setNewMenuItemName(e.target.value)}
        className="w-full mb-4"
      />
      <Button onClick={onSave} backgroundColor="#253BFF" className="text-white w-full">
        Save
      </Button>
    </div>
  );
};

export default AddNewMenuItemForm;
