import React, { useState } from 'react';
import Button from '../atoms/Button';
import Select from '../atoms/Select';
import MenuList from '../molecules/MenuList';
import { useMenus, useMenuItems, useAddMenuItem, MenuItem as MenuItemType } from '../../hooks/apiHooks';
import AddMenuForm from './AddMenuForm';
import { toast } from 'react-toastify';

const MenusSection: React.FC = () => {
  const { data: menus, refetch: refetchMenus } = useMenus();
  const [selectedMenu, setSelectedMenu] = useState<string | null>(null);
  const [selectedParentItem, setSelectedParentItem] = useState<MenuItemType | null>(null);
  const { data: menuItems = [] } = useMenuItems(selectedMenu || '');
  const addMenuItemMutation = useAddMenuItem(selectedMenu || '');

  const handleAddItem = (parentItem: MenuItemType) => {
    setSelectedParentItem(parentItem);
  };

  const handleSaveMenuItem = async (name: string) => {
    if (!name.trim()) {
      toast.error('Please enter a name');
      return;
    }

    const newItem = {
      menuId: selectedMenu || '',
      parentId: selectedParentItem?.id || null,
      name,
      depth: (selectedParentItem?.depth || 0) + 1,
    };

    try {
      await addMenuItemMutation.mutateAsync(newItem);
      toast.success('Menu item added successfully');
      setSelectedParentItem(null);
    } catch (error) {
      toast.error('Failed to add menu item. Please try again.');
    }
  };

  return (
    <div>
      <Select
        value={selectedMenu || ''}
        onChange={(e) => setSelectedMenu(e.target.value)}
        options={menus?.map((menu) => ({ label: menu.name, value: menu.id })) || []}
        className="max-md:w-full w-[50%] mb-5"
      />

      {selectedMenu && (
        <MenuList items={menuItems} onAdd={handleAddItem} />
      )}

      {selectedParentItem && (
        <Modal onClose={() => setSelectedParentItem(null)}>
          <AddMenuForm
            menuId={selectedMenu || ''}
            parentId={selectedParentItem.id}
            depth={selectedParentItem.depth + 1}
            onSave={handleSaveMenuItem}
          />
        </Modal>
      )}
    </div>
  );
};

export default MenusSection;
