import React, { useState } from 'react';
import MenuItem from './MenuItem';
import { MenuItem as MenuItemType } from '../../hooks/apiHooks';

interface MenuListProps {
  items: MenuItemType[];
  onAdd: (item: MenuItemType) => void;
}

const MenuList: React.FC<MenuListProps> = ({ items, onAdd }) => {
  const [expandedItems, setExpandedItems] = useState<{ [key: string]: boolean }>({});

  const handleToggleExpand = (id: string) => {
    setExpandedItems((prev) => ({
      ...prev,
      [id]: !prev[id],
    }));
  };

  const renderItems = (parentId: string | null, depth: number) => {
    return items
      .filter((item) => item.parentId === parentId)
      .map((item) => (
        <div key={item.id} className="pl-4">
          <MenuItem
            item={{ ...item, depth }}
            onAdd={() => onAdd(item)}
            onToggleExpand={() => handleToggleExpand(item.id)}
            isExpanded={expandedItems[item.id] || false}
          />
          {expandedItems[item.id] && renderItems(item.id, depth + 1)}
        </div>
      ));
  };

  return <div className="space-y-2">{renderItems(null, 0)}</div>;
};

export default MenuList;
