import React, { useState } from 'react';
import Sidebar from '../organisms/Sidebar';
import MenusSection from '../organisms/MenusSection';
import AddMenuForm from '../organisms/AddMenuForm';

const Layout: React.FC = () => {
  const [isSidebarOpen, setIsSidebarOpen] = useState<boolean>(true);

  return (
    <div className="flex h-screen flex-">
      <Sidebar isOpen={isSidebarOpen} onToggle={() => setIsSidebarOpen(!isSidebarOpen)} />
      <div className="flex-grow flex">
        <header className="bg-white shadow-sm z-10">
              <div className="max-w-7xl mx-auto py-4 px-4 sm:px-6 lg:px-8">
                <h1 className="text-lg font-semibold text-gray-900">Menu Management</h1>
              </div>
            </header>
        <MenusSection />
        {isSidebarOpen && <AddMenuForm />}
      </div>
    </div>
  );
};

export default Layout;
