import React, { useState } from 'react';
import Button from '../atoms/Button';
import { MenuItem as MenuItemType } from '../../hooks/apiHooks';

interface MenuItemProps {
  item: MenuItemType;
  onAdd: () => void;
  onToggleExpand: () => void;
  isExpanded: boolean;
}

const MenuItem: React.FC<MenuItemProps> = ({ item, onAdd, onToggleExpand, isExpanded }) => {
  const [isHovered, setIsHovered] = useState<boolean>(false);

  return (
    <div
      className="flex items-center justify-between pl-4"
      onMouseEnter={() => setIsHovered(true)}
      onMouseLeave={() => setIsHovered(false)}
    >
      <div className="flex items-center">
        {item.depth > 0 && <span className="mr-2">{'—'.repeat(item.depth)}</span>}
        <span onClick={onToggleExpand} className="cursor-pointer">
          {isExpanded ? '-' : '+'} {item.name}
        </span>
      </div>
      {isHovered && (
        <Button onClick={onAdd} className="ml-2" backgroundColor=''>
          +
        </Button>
      )}
    </div>
  );
};

export default MenuItem;
