import { Link, useLocation } from "react-router-dom";
interface SidebarItemProps {
    img: string;
    label: string;
    to: string;
  }

  const SidebarItem: React.FC<SidebarItemProps> = ({ img, label, to }) => {
    const location = useLocation();
    const isActive = location.pathname === to;

    return (
      <Link
        to={to}
        className={`flex items-center p-2 rounded-lg mb-2 ${
          isActive ? "bg-white rounded-s-full" : ""
        }`}
      >
        <div className="flex items-center justify-center w-[32px] h-[32px] bg-blue-250 rounded-[50px]">
          <img src={img} />
        </div>
        <span
          className={`ml-1 text-[14px]  leading-[14px] ${
            isActive
              ? "bg-white rounded-s-full text-blue font-[700]"
              : "font-[400]"
          }`}
        >
          {label}
        </span>
      </Link>
    );
  };

  export default SidebarItem
