import React from 'react';
import MenuItem from './MenuItem';

interface MenuListProps {
  items: MenuItemType[];
  onAdd: (item: MenuItemType) => void;
  expandedItems: { [key: string]: boolean };
  onToggleExpand: (id: string) => void;
}

const MenuList: React.FC<MenuListProps> = ({ items, onAdd, expandedItems, onToggleExpand }) => {
  const menuTree = buildMenuTree(items);

  const renderItems = (items: MenuItemType[]) => {
    return items.map((item) => (
      <div key={item.id} className="pl-4">
        <MenuItem
          item={item}
          onAdd={() => onAdd(item)}
          onToggleExpand={() => onToggleExpand(item.id)}
          isExpanded={expandedItems[item.id] || false}
        />
        {expandedItems[item.id] && item.children && item.children.length > 0 && (
          <div>{renderItems(item.children)}</div>
        )}
      </div>
    ));
  };

  return <div className="space-y-2">{renderItems(menuTree)}</div>;
};

export default MenuList;
