// export default MenuItem;


// import React from 'react';

interface MenuItemProps {
  item: MenuItemType;
  onAdd: () => void;
  onToggleExpand: () => void;
  isExpanded: boolean;
}

const MenuItem: React.FC<MenuItemProps> = ({ item, onAdd, onToggleExpand, isExpanded }) => {
    const [isHovered, setIsHovered] = useState<boolean>(false);

  return (
    <div className="flex items-center relative" onMouseEnter={() => setIsHovered(true)}
    onMouseLeave={() => setIsHovered(false)}>
      {/* Chevron Icon */}
      <div onClick={onToggleExpand} className="cursor-pointer mr-2">
        {isExpanded ? (
           <svg xmlns="http://www.w3.org/2000/svg" width="16" height="16" fill="currentColor" className="bi bi-chevron-down mt-4" viewBox="0 0 16 16">
           <path fill-rule="evenodd" d="M1.646 4.646a.5.5 0 0 1 .708 0L8 10.293l5.646-5.647a.5.5 0 0 1 .708.708l-6 6a.5.5 0 0 1-.708 0l-6-6a.5.5 0 0 1 0-.708"/>
         </svg>
        ) : (

          <svg xmlns="http://www.w3.org/2000/svg" width="16" height="16" fill="currentColor" className="bi bi-chevron-right mt-4" viewBox="0 0 16 16">
          <path fill-rule="evenodd" d="M4.646 1.646a.5.5 0 0 1 .708 0l6 6a.5.5 0 0 1 0 .708l-6 6a.5.5 0 0 1-.708-.708L10.293 8 4.646 2.354a.5.5 0 0 1 0-.708"/>
        </svg>
        )}
      </div>

      {/* Menu Item Name */}
      <span className="font-[400] text-[14px] leading-[14px] mt-4" >{item.name}</span>

      {/* Add Button */}
      {isHovered && (
      <button
        onClick={onAdd}
        className="ml-2 p-1 bg-blue-500 text-white rounded hover:bg-blue-600"
      >
        +
      </button>
      )}

      {/* Vertical Line for Child Items */}
      {/* {item.children && item.children.length > 0 && (
        <div className="absolute left-0 top-full w-px h-4 bg-gray-300"></div>
      )} */}
    </div>
  );
};

export default MenuItem;
